import React, { Component } from "react";
import Players from "./players";
// import DayPicker from "./common/date-picker";

class Games extends Component {
  state = {
    players: [
      { id: 1, name: "Bibs", selected: true },
      { id: 2, name: "Vasili", selected: false },
      { id: 3, name: "Rami", selected: false },
      { id: 4, name: "Asad", selected: false },
    ],
  };

  selectPlayer(playerId) {
    let { players } = this.state;
    players[playerId - 1].selected = true;
    console.log(players[playerId - 1]);
    this.setState({ players });
  }

  render() {
    const { players } = this.state;

    return (
      <div>
        <Players
          players={players}
          selectPlayer={() => this.selectPlayer(players.id)}
        />
        <p>{players}</p>
        <table border="4">
          <thead>
            <tr>
              <td>id</td>
              <td>name</td>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td></td>
              <td>{players.selected && players.name}</td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

export default Games;
